$(document).ready(function(){
    $("#mobile-menu-open").click(function(){
	$("#mobile-menu-open").toggle(300, "linear");
	$("#mobile-menu-close").toggle(300, "linear");
    });
    $("#mobile-menu-close").click(function(){
	$("#mobile-menu-close").toggle(300, "linear");
	$("#mobile-menu-open").toggle(300, "linear");
    });
    $("#send-button").click(function(){
	$("#success").show();
    });
});
			       
